con:configuration.scriptText:
//check response time
assert messageExchange.timeTaken  < 4000 

//check for Endpoint

def endpint="http://webservices.oorsprong.org/websamples.countryinfo/CountryInfoService.wso"
assert messageExchange.getEndpoint()==endpint
//log.info messageExchange.getEndpoint() 


//check for header

assert messageExchange.responseHeaders["Content-Length"] != 0
log.info (messageExchange.responseHeaders["Content-Length"])


//check attachments
assert messageExchange.responseAttachments.length == 0
log.info (messageExchange.responseAttachments.length)


//validate response nodes
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def requsetHolder = groovyUtils.getXmlHolder( messageExchange.requestContent )
def responseHolder = groovyUtils.getXmlHolder( messageExchange.responseContent )
def refNum = responseHolder.getNodeValue("//m:CountryCurrencyResult/m:sName")
assert refNum == "Rupees"

//to get response
def resp = messageExchange.responseContentAsXml.toString()
log.info resp

log.info("------------------------//------------------")
log.info log.name

//get json response
import groovy.json.JsonSlurper
def responseMessage = messageExchange.response.responseContent
def json = new JsonSlurper().parseText(responseMessage)

//assert node values

assert json.capital.toString() != null
assert json.name.common.toString() == "[Taiwan]"

//to get the Test Step Name
testStepName = messageExchange.modelItem.testStep.name
log.info testStepName

//to store the response as Xml string
xmlHold = messageExchange.responseContentAsXml.toString() 
log.info xmlHold

con:soapui-project.con:afterLoadScript:
def su = new File("setup").text 
def td = new File("teardown").text 

project.getTestSuiteList().each
{ 
testSuite -> testSuite.getTestCaseList().each
{ 
testCase -> testCase.setSetupScript(su) 
testCase.setTearDownScript(td) 
} 
}
